  "old_string": ".accent-text {
    color: #dc2626;
    position: relative;
    display: inline-block;
}

.accent-text::after {
    content: '';
    position: absolute;
    width: 0;
    height: 3px;
    bottom: 0;
    left: 0;
    background-color: #dc2626;
    transition: width 0.8s ease;
}

.accent-text.animate::after {
    width: 100%;
}",
  "new_string": ".accent-text {
    color: #dc2626;
    position: relative;
    display: inline-block;
    overflow: hidden;
}

.accent-text::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(220, 38, 38, 0.3), transparent);
    transition: left 0.8s ease;
}

.accent-text::after {
    content: '';
    position: absolute;
    width: 0;
    height: 3px;
    bottom: 0;
    left: 0;
    background-color: #dc2626;
    transition: width 0.8s ease;
}

.accent-text.animate::before {
    left: 100%;
}

.accent-text.animate::after {
    width: 100%;
}"
}

