/* City 16-9 Gallery - Enhanced Styles */


/* Color and Font Definitions */

:root {
    --neon-datagreen: #00FF9D;
    /* Bright green */
    --oil-slick-black: #0F0F0F;
    /* Dark background */
    --error-red: #FF003D;
    /* Alert color */
    --quantum-blue: #00D4FF;
    /* Primary theme color */
    --quantum-purple: #8B5CF6;
    /* Secondary tone */
    --quantum-cyan: #06B6D4;
    /* Accent color */
    --quantum-pink: #EC4899;
    /* Highlight color */
    --portal-gold: #FFD700;
    /* Special highlight color */
    --font-primary: 'Oswald', sans-serif;
    /* Main font family */
    --font-display: 'Oswald', sans-serif;
    /* Display font family */
    /* Spacing values for responsive layouts */
    --spacing-xs: 0.5rem;
    --spacing-sm: 1rem;
    --spacing-md: 1.5rem;
    --spacing-lg: 2rem;
    --spacing-xl: 3rem;
    --spacing-xxl: 4rem;
}


/* Global Reset and Base Setup */

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    /* Consistent box model */
}

body {
    font-family: var(--font-primary);
    /* Assigning primary font */
    font-family: var(--font-primary);
    background: var(--oil-slick-black);
    color: var(--quantum-blue);
    line-height: 1.6;
    overflow-x: hidden;
}


/* General styles */

a {
    text-decoration: none;
}

img {
    pointer-events: none;
}

img:after {
    content: "";
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: transparent;
    pointer-events: all;
}


/* Black nav white hover link */

.navbar {
    background-color: #000;
    border-bottom: 1px solid #333;
}

.navbar-brand {
    color: #fff;
    font-size: 1.5rem;
    font-weight: 700;
    text-shadow: 0 0 20px var(--quantum-blue);
}

.nav-link {
    color: #fff;
    font-weight: 600;
    transition: all 0.3s ease;
}

.nav-link:hover {
    color: #fff;
    background-color: #333;
    border-radius: 4px;
}


/* Back to top and jump down buttons */

#myBtn,
#jumpBtn {
    display: none;
    position: fixed;
    right: 30px;
    z-index: 99;
    font-size: 16px;
    border: none;
    outline: none;
    background-color: black;
    color: rgb(255, 0, 0);
    cursor: pointer;
    padding: 10px;
    border-radius: 4px;
    transition: all 0.3s ease;
}

#myBtn {
    bottom: 20px;
}

#jumpBtn {
    bottom: 60px;
}

#myBtn:hover,
#jumpBtn:hover {
    background-color: #555;
    transform: translateY(-2px);
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
}


/* Gallery styles */

.gallery {
    display: flex;
    flex-wrap: wrap;
    padding: var(--spacing-md);
}

.gallery>a,
.gallery::after {
    flex-basis: var(--w);
}

.gallery>a {
    margin: 0.25rem;
    flex-grow: calc(var(--w) / var(--h) * 100);
    width: calc(var(--w) * 1px);
    transition: all 0.3s ease;
    border-radius: 8px;
    overflow: hidden;
}

.gallery>a:hover {
    transform: scale(1.02);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.3);
}

.gallery::after {
    --w: 2;
    --h: 1;
    content: '';
    flex-grow: 1000000;
}

.gallery>a>img {
    display: block;
    width: 100%;
    height: auto;
    border-radius: 8px;
    transition: all 0.3s ease;
}

.gallery>a:hover>img {
    filter: brightness(1.1);
}


/* Header styles */

.header-image {
    height: 400px;
    color: #000000;
    padding: 0px;
}

.header-info {
    background-color: rgba(0, 0, 0, 0.5);
    width: 100%;
    height: 100%;
    padding-top: 100px;
    text-align: center;
}

.header-info-details h1 {
    font-size: 56px;
}

.header-info-details {
    border-top: 1px solid #eee;
    width: 80%;
    margin: 50px auto 0px;
    padding-top: 50px;
    font-size: 20px;
}


/* Gallery section styles */

.gallery-section h2 {
    text-align: center;
    margin-top: 30px;
    font-size: 2.5rem;
    color: var(--quantum-blue);
    text-shadow: 0 0 20px var(--quantum-blue);
}

.gallery-section h2 a {
    visibility: hidden;
    color: #aaaaaa;
}

.gallery-section h2 a:hover {
    color: #0056b3;
}

.gallery-section:hover h2 a {
    visibility: visible;
}

.gallery-section h2 a svg {
    height: 1.2rem;
}

.gallery-section p {
    width: 70%;
    margin: 0px auto 20px;
    text-align: center;
    color: #ccc;
    font-size: 1.1rem;
    line-height: 1.6;
}


/* Caption date styles */

.caption-date {
    font-size: 15px;
    font-weight: normal;
    margin-top: 3px;
    margin-bottom: 0px;
}


/* Center the footer content */

footer {
    text-align: center;
    margin-top: 30px;
    padding: var(--spacing-xl) 0;
    background: rgba(15, 15, 15, 0.9);
    border-top: 1px solid #333;
}

footer a {
    text-decoration: none;
    color: var(--quantum-blue);
    font-weight: 600;
    transition: all 0.3s ease;
}

footer a:hover {
    color: var(--quantum-cyan);
    text-shadow: 0 0 10px var(--quantum-cyan);
}


/* Responsive Design */

@media (max-width: 768px) {
    .gallery {
        padding: var(--spacing-sm);
    }
    .gallery>a {
        margin: 0.125rem;
    }
    .gallery-section h2 {
        font-size: 2rem;
    }
    .gallery-section p {
        width: 90%;
        font-size: 1rem;
    }
    #myBtn,
    #jumpBtn {
        right: 15px;
        padding: 8px;
        font-size: 14px;
    }
    #myBtn {
        bottom: 15px;
    }
    #jumpBtn {
        bottom: 50px;
    }
}

@media (max-width: 480px) {
    .gallery>a {
        flex-basis: 100%;
        margin: 0.125rem 0;
    }
    .gallery-section h2 {
        font-size: 1.5rem;
    }
}


/* Animations */

@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.fade-in-up {
    animation: fadeInUp 0.6s ease-out;
}


/* Scrollbar Styling */

::-webkit-scrollbar {
    width: 8px;
}

::-webkit-scrollbar-track {
    background: var(--oil-slick-black);
}

::-webkit-scrollbar-thumb {
    background: var(--quantum-blue);
    border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
    background: var(--quantum-cyan);
}


/* Loading Animation */

.loading {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 200px;
}

.loading::after {
    content: '';
    width: 40px;
    height: 40px;
    border: 4px solid #333;
    border-top: 4px solid var(--quantum-blue);
    border-radius: 50%;
    animation: spin 1s linear infinite;
}

@keyframes spin {
    0% {
        transform: rotate(0deg);
    }
    100% {
        transform: rotate(360deg);
    }
}


/* Error State */

.error {
    text-align: center;
    padding: var(--spacing-xl);
    color: var(--error-red);
}

.error h3 {
    margin-bottom: var(--spacing-md);
}

.error p {
    color: #ccc;
}


/* Success State */

.success {
    text-align: center;
    padding: var(--spacing-xl);
    color: var(--neon-datagreen);
}

.success h3 {
    margin-bottom: var(--spacing-md);
}

.success p {
    color: #ccc;
}
