/* General styles */

a {
    text-decoration: none;
}

img {
    pointer-events: none;
}

img:after {
    content: "";
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: transparent;
    pointer-events: all;
}


/* Black nav white hover link */

.navbar {
    background-color: #000;
    border-bottom: 1px solid #333;
}

.navbar-brand {
    color: #fff;
}

.nav-link {
    color: #fff;
    font-weight: 600;
}

.nav-link:hover {
    color: #fff;
    background-color: #333;
    border-radius: 4px;
}


/* Back to top and jump down buttons */

#myBtn,
#jumpBtn {
    display: none;
    position: fixed;
    right: 30px;
    z-index: 99;
    font-size: 16px;
    border: none;
    outline: none;
    background-color: black;
    color: rgb(255, 0, 0);
    cursor: pointer;
    padding: 10px;
    border-radius: 4px;
}

#myBtn {
    bottom: 70px;
}

#jumpBtn {
    bottom: 60px;
}

#myBtn:hover,
#jumpBtn:hover {
    background-color: #555;
}


/* Body styles */

body {
    background-color: #202020;
    font-family: 'Oswald', sans-serif;
    color: #fff;
    text-shadow: 2px 2px 2px #000;
}


/* Gallery styles */

.gallery {
    display: flex;
    flex-wrap: wrap;
}

.gallery>a,
.gallery::after {
    flex-basis: var(--w);
}

.gallery>a {
    margin: 0.25rem;
    flex-grow: calc(var(--w) / var(--h) * 100);
    width: calc(var(--w) * 1px);
}

.gallery::after {
    --w: 2;
    --h: 1;
    content: '';
    flex-grow: 1000000;
}

.gallery>a>img {
    display: block;
    width: 100%;
}


/* Header styles */

.header-image {
    height: 400px;
    color: #000000;
    padding: 0px;
}

.header-info {
    background-color: rgba(0, 0, 0, 0.5);
    width: 100%;
    height: 100%;
    padding-top: 100px;
    text-align: center;
}

.header-info-details h1 {
    font-size: 56px;
}

.header-info-details {
    border-top: 1px solid #eee;
    width: 80%;
    margin: 50px auto 0px;
    padding-top: 50px;
    font-size: 20px;
}


/* Gallery section styles */

.gallery-section h2 {
    text-align: center;
    margin-top: 30px;
}

.gallery-section h2 a {
    visibility: hidden;
    color: #aaaaaa;
}

.gallery-section h2 a:hover {
    color: #0056b3;
}

.gallery-section:hover h2 a {
    visibility: visible;
}

.gallery-section h2 a svg {
    height: 1.2rem;
}

.gallery-section p {
    width: 70%;
    margin: 0px auto 20px;
}


/* Caption date styles */

.caption-date {
    font-size: 15px;
    font-weight: normal;
    margin-top: 3px;
    margin-bottom: 0px;
}


/* Center the footer content */

footer {
    text-align: center;
    margin-top: 30px;
}

footer a {
    text-decoration: none;
    color: #ffffff;
}

footer a:hover {
    color: #aaaaaa;
}
