parameters.jsCode:
// Process repurposed content
const repurposed = $('Repurpose Content').item.json.choices[0].message.content;
const original = $('Webhook').item.json.original_content;

// Parse platforms
const platforms = {
  youtube_shorts: extractSection(repurposed, 'YouTube Shorts'),
  tiktok: extractSection(repurposed, 'TikTok'),
  instagram_reels: extractSection(repurposed, 'Instagram Reels'),
  twitter: extractSection(repurposed, 'Twitter'),
  linkedin: extractSection(repurposed, 'LinkedIn')
};

const result = {
  original_content: original,
  repurposed_content: repurposed,
  platforms: platforms,
  timestamp: new Date().toISOString(),
  version: 'pro',
  features: [
    'multi-platform',
    'ai-optimization',
    'batch-processing',
    'custom-templates'
  ]
};

function extractSection(text, platform) {
  const regex = new RegExp(`${platform}[\s\S]*?(?=\n\n[A-Z]|$)`, 'i');
  const match = text.match(regex);
  return match ? match[0].trim() : null;
}

return result;
