"""
Generate HTML Documentation System from Directory Analysis
"""

import csv
import json
import os
from pathlib import Path
from datetime import datetime


def load_analysis_data(csv_file_path):
    """Load the directory analysis data from CSV file"""
    data = []
    with open(csv_file_path, 'r', encoding='utf-8') as csvfile:
        reader = csv.DictReader(csvfile)
        for row in reader:
            data.append(row)
    return data


def categorize_file(file_path):
    """Categorize a file based on its extension"""
    path_obj = Path(file_path)
    ext = path_obj.suffix.lower()

    if ext in ['.py', '.js', '.ts', '.java', '.cpp', '.c', '.h', '.rb', '.go', '.rs']:
        return 'code'
    elif ext in ['.md', '.txt', '.rst', '.tex']:
        return 'documentation'
    elif ext in ['.json', '.yaml', '.yml', '.xml', '.csv', '.tsv']:
        return 'data'
    elif ext in ['.jpg', '.jpeg', '.png', '.gif', '.bmp', '.svg', '.webp']:
        return 'image'
    elif ext in ['.mp4', '.avi', '.mkv', '.mov', '.wmv', '.flv']:
        return 'video'
    elif ext in ['.mp3', '.wav', '.flac', '.aac', '.ogg']:
        return 'audio'
    elif ext in ['.zip', '.tar', '.gz', '.rar', '.7z', '.bz2']:
        return 'archive'
    elif ext in ['.pdf', '.doc', '.docx', '.ppt', '.pptx']:
        return 'document'
    elif ext in ['.env', '.config', '.conf', '.ini']:
        return 'configuration'
    else:
        return 'other'


def convert_size_to_bytes(size_str):
    """Convert size string to bytes for sorting"""
    size_str = size_str.replace(',', '').lower()

    if 'kb' in size_str:
        return float(size_str.replace('kb', '')) * 1024
    elif 'mb' in size_str:
        return float(size_str.replace('mb', '')) * 1024 * 1024
    elif 'gb' in size_str:
        return float(size_str.replace('gb', '')) * 1024 * 1024 * 1024
    elif 'bytes' in size_str:
        return float(size_str.replace(' bytes', '').replace('byte', ''))
    else:
        # Assume it's already in bytes if no unit specified
        try:
            return float(size_str)
        except ValueError:
            return 0


def generate_js_data(data):
    """Generate JavaScript data structure from analysis data"""
    js_data = []

    for row in data:
        # Skip root directory entry
        if row['path'] == '/Users/steven':
            continue

        item = {
            'path': row['path'],
            'type': row['type'],
            'size': row['size_formatted'],
            'modified': row['modified'],
            'category': row['category'] if row['category'] != 'directory' else 'project' if 'AVATARARTS' in row['path'] else 'directory',
            'depth': int(row['depth']),
            'description': f"{'File' if row['type'] == 'file' else 'Directory'} in the system"
        }

        # If category is 'directory', determine a more specific category
        if row['type'] == 'directory':
            if 'AVATARARTS' in row['path']:
                item['category'] = 'project'
            elif any(keyword in row['path'].lower() for keyword in ['doc', 'manual', 'guide']):
                item['category'] = 'documentation'
            elif any(keyword in row['path'].lower() for keyword in ['script', 'tool', 'util']):
                item['category'] = 'project'
            else:
                item['category'] = 'directory'
        else:
            # For files, use the categorization function if not already categorized
            if row['category'] == 'other':
                item['category'] = categorize_file(row['path'])

        js_data.append(item)

    return js_data


def create_main_html():
    """Create the main HTML file"""
    html_content = '''<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>AVATARARTS - Directory Documentation System</title>
    <link rel="stylesheet" href="styles.css">
    <link href="https://fonts.googleapis.com/css2?family=Roboto:wght@300;400;500;700&display=swap" rel="stylesheet">
</head>
<body>
