        if not self.performance_file.exists():
            performance_headers = [
                'opportunity_id', 'metric_name', 'value', 'target', 
                'date_recorded', 'status', 'notes'
            ]
            pd.DataFrame(columns=performance_headers).to_csv(self.performance_file, index=False)

