parameters.functionCode:
// Process portfolio content for Behance
const input = $input.first().json;
const content = input.content || {};

// Behance-specific formatting
const behanceProject = {
  title: content.title || 'Untitled Project',
  description: content.description || '',
  tags: content.tags || [],
  category: content.category || 'Digital Art',
  tools: content.tools || ['Photoshop', 'Illustrator'],
  images: content.images || [],
  cover_image: content.cover_image || content.images[0],
  project_type: content.project_type || 'Personal Project',
  client: content.client || null,
  year: content.year || new Date().getFullYear(),
  featured: content.featured || false,
  behance_metadata: {
    created_at: new Date().toISOString(),
    updated_at: new Date().toISOString(),
    status: 'draft',
    visibility: 'public',
    allow_comments: true,
    allow_appreciations: true
  }
};

// Validate required fields
const validation = {
  valid: true,
  errors: []
};

if (!behanceProject.title) {
  validation.errors.push('Title is required');
  validation.valid = false;
}

if (!behanceProject.cover_image) {
  validation.errors.push('Cover image is required');
  validation.valid = false;
}

if (behanceProject.images.length === 0) {
  validation.errors.push('At least one project image is required');
  validation.valid = false;
}

return [{
  json: {
    ...input,
    behance_project: behanceProject,
    validation: validation,
    ready_for_upload: validation.valid
  }
}];

// Create social media promotion content
const project = $input.first().json;
const behanceUrl = `https://www.behance.net/gallery/${project.project_id}`;

const socialContent = {
  twitter: {
    text: `Check out my latest project: "${project.title}" on Behance! ${behanceUrl} #design #art #behance`,
    hashtags: ['#design', '#art', '#behance', '#creative', '#portfolio']
  },
  linkedin: {
    text: `I'm excited to share my latest creative project: "${project.title}"

${project.description}

View the full project on Behance: ${behanceUrl}

#design #art #creative #portfolio #behance`,
    hashtags: ['#design', '#art', '#creative', '#portfolio', '#behance']
  },
  instagram: {
    caption: `âœ¨ New project alert! âœ¨

"${project.title}"

${project.description}

Swipe to see more details! ðŸ‘†

#design #art #creative #behance #portfolio`,
    hashtags: ['#design', '#art', '#creative', '#behance', '#portfolio', '#inspiration']
  },
  facebook: {
    message: `I'm thrilled to share my latest creative project: "${project.title}"

${project.description}

Check out the full project on Behance: ${behanceUrl}`,
    hashtags: ['#design', '#art', '#creative', '#portfolio']
  }
};

return [{
  json: {
    ...project,
    social_content: socialContent,
    behance_url: behanceUrl
  }
}];

// Track portfolio performance across platforms
const project = $input.first().json;
const timestamp = new Date().toISOString();

const analytics = {
  project_id: project.project_id,
  title: project.title,
  platforms: {
    behance: {
      url: project.behance_url,
      published_at: timestamp,
      status: 'published'
    },
    linkedin: {
      post_id: project.linkedin_post_id || null,
      published_at: timestamp,
      status: project.linkedin_post_id ? 'published' : 'pending'
    },
    twitter: {
      tweet_id: project.twitter_tweet_id || null,
      published_at: timestamp,
      status: project.twitter_tweet_id ? 'published' : 'pending'
    },
    instagram: {
      media_id: project.instagram_media_id || null,
      published_at: timestamp,
      status: project.instagram_media_id ? 'published' : 'pending'
    }
  },
  metrics: {
    total_platforms: 4,
    published_platforms: 0,
    pending_platforms: 0
  },
  created_at: timestamp
};

// Count published platforms
for (const [platform, data] of Object.entries(analytics.platforms)) {
  if (data.status === 'published') {
    analytics.metrics.published_platforms++;
  } else {
    analytics.metrics.pending_platforms++;
  }
}

return [{ json: analytics }];

