// Optimize LinkedIn content for engagement
const content = $input.first().json;

const optimizedContent = {
  original: content.text,
  optimized: content.text,
  hashtags: [],
  mentions: [],
  call_to_action: '',
  engagement_score: 0
};

// Extract and optimize hashtags
const hashtagRegex = /#\w+/g;
const extractedHashtags = content.text.match(hashtagRegex) || [];

// Add relevant hashtags if missing
const suggestedHashtags = [
  '#Python', '#AI', '#Automation', '#Technology', '#Programming',
  '#MachineLearning', '#DataScience', '#Innovation', '#Tech', '#Developer'
];

optimizedContent.hashtags = [...new Set([...extractedHashtags, ...suggestedHashtags])].slice(0, 5);

// Extract mentions
const mentionRegex = /@\w+/g;
optimizedContent.mentions = content.text.match(mentionRegex) || [];

// Add call to action
const ctaOptions = [
  'What are your thoughts on this?',
  'Have you tried this approach?',
  'What challenges have you faced?',
  'Share your experience below!',
  'Let me know what you think!'
];
optimizedContent.call_to_action = ctaOptions[Math.floor(Math.random() * ctaOptions.length)];

// Calculate engagement score
let score = 0;
score += optimizedContent.hashtags.length * 2; // 2 points per hashtag
score += optimizedContent.mentions.length * 3; // 3 points per mention
score += content.text.length > 100 ? 5 : 0; // 5 points for longer content
score += content.text.includes('?') ? 3 : 0; // 3 points for questions
score += content.text.includes('!') ? 2 : 0; // 2 points for excitement

optimizedContent.engagement_score = Math.min(score, 20); // Cap at 20

// Add CTA to content if engagement score is low
if (optimizedContent.engagement_score < 10) {
  optimizedContent.optimized += `\n\n${optimizedContent.call_to_action}`;
}

// Add hashtags to content
if (optimizedContent.hashtags.length > 0) {
  optimizedContent.optimized += `\n\n${optimizedContent.hashtags.join(' ')}`;
}

return [{
  json: {
    ...content,
    optimization: optimizedContent
  }
}];

// Track LinkedIn post engagement
const post = $input.first().json;
const timestamp = new Date().toISOString();

const engagement = {
  post_id: post.id,
  content: post.optimization.optimized,
  hashtags: post.optimization.hashtags,
  mentions: post.optimization.mentions,
  call_to_action: post.optimization.call_to_action,
  engagement_score: post.optimization.engagement_score,
  published_at: timestamp,
  metrics: {
    likes: 0,
    comments: 0,
    shares: 0,
    views: 0,
    clicks: 0
  },
  performance: {
    engagement_rate: 0,
    reach: 0,
    impressions: 0
  }
};

return [{ json: engagement }];

// Adapt content for different platforms
const input = $input.first().json;
const content = input.content || {};
const platforms = input.platforms || [];

const adaptedContent = {};

// LinkedIn adaptation
if (platforms.includes('linkedin')) {
  adaptedContent.linkedin = {
    text: content.text,
    hashtags: content.hashtags || [],
    mentions: content.mentions || [],
    call_to_action: content.call_to_action || '',
    max_length: 3000,
    format: 'professional'
  };
}

// Twitter adaptation
if (platforms.includes('twitter')) {
  adaptedContent.twitter = {
    text: content.text.length > 280 ? content.text.substring(0, 277) + '...' : content.text,
    hashtags: (content.hashtags || []).slice(0, 3),
    mentions: content.mentions || [],
    max_length: 280,
    format: 'concise'
  };
}

// Instagram adaptation
if (platforms.includes('instagram')) {
  adaptedContent.instagram = {
    caption: content.text,
    hashtags: (content.hashtags || []).slice(0, 30),
    mentions: content.mentions || [],
    max_length: 2200,
    format: 'visual'
  };
}

// Facebook adaptation
if (platforms.includes('facebook')) {
  adaptedContent.facebook = {
    message: content.text,
    hashtags: content.hashtags || [],
    mentions: content.mentions || [],
    max_length: 63206,
    format: 'casual'
  };
}

// Behance adaptation
if (platforms.includes('behance')) {
  adaptedContent.behance = {
    title: content.title || 'Untitled Project',
    description: content.text,
    tags: content.hashtags || [],
    category: content.category || 'Digital Art',
    tools: content.tools || [],
    format: 'portfolio'
  };
}

return [{
  json: {
    ...input,
    adapted_content: adaptedContent
  }
}];

// Aggregate results from all platforms
const results = $input.all();
const timestamp = new Date().toISOString();

const aggregatedResults = {
  distribution_id: Math.random().toString(36).substr(2, 9),
  original_content: results[0].json.content || {},
  platforms: {},
  success_count: 0,
  failure_count: 0,
  total_platforms: 0,
  distributed_at: timestamp
};

// Process each platform result
for (const result of results) {
  const platform = result.json.platform || 'unknown';
  const success = result.json.success !== false;

  aggregatedResults.platforms[platform] = {
    success: success,
    post_id: result.json.id || result.json.post_id || null,
    url: result.json.url || null,
    error: result.json.error || null,
    published_at: timestamp
  };

  aggregatedResults.total_platforms++;

  if (success) {
    aggregatedResults.success_count++;
  } else {
    aggregatedResults.failure_count++;
  }
}

// Calculate success rate
aggregatedResults.success_rate = (aggregatedResults.success_count / aggregatedResults.total_platforms) * 100;

return [{ json: aggregatedResults }];

