// Collect metrics from all platforms
const platforms = ['linkedin', 'twitter', 'instagram', 'facebook', 'behance'];
const metrics = {};
const timestamp = new Date().toISOString();

// Simulate API calls to each platform
for (const platform of platforms) {
  try {
    // This would be replaced with actual API calls
    metrics[platform] = {
      platform: platform,
      timestamp: timestamp,
      followers: Math.floor(Math.random() * 10000) + 1000,
      posts_count: Math.floor(Math.random() * 100) + 10,
      engagement_rate: Math.random() * 10 + 1,
      reach: Math.floor(Math.random() * 50000) + 5000,
      impressions: Math.floor(Math.random() * 100000) + 10000,
      likes: Math.floor(Math.random() * 1000) + 100,
      comments: Math.floor(Math.random() * 100) + 10,
      shares: Math.floor(Math.random() * 50) + 5,
      clicks: Math.floor(Math.random() * 200) + 20,
      status: 'success'
    };
  } catch (error) {
    metrics[platform] = {
      platform: platform,
      timestamp: timestamp,
      error: error.message,
      status: 'error'
    };
  }
}

return [{ json: { platforms: metrics, collected_at: timestamp } }];

// Analyze social media metrics
const data = $input.first().json;
const platforms = data.platforms;
const timestamp = data.collected_at;

const analysis = {
  timestamp: timestamp,
  total_platforms: Object.keys(platforms).length,
  successful_platforms: 0,
  failed_platforms: 0,
  total_followers: 0,
  total_engagement: 0,
  total_reach: 0,
  total_impressions: 0,
  platform_performance: {},
  insights: [],
  recommendations: []
};

// Analyze each platform
for (const [platform, metrics] of Object.entries(platforms)) {
  if (metrics.status === 'success') {
    analysis.successful_platforms++;
    analysis.total_followers += metrics.followers || 0;
    analysis.total_engagement += metrics.engagement_rate || 0;
    analysis.total_reach += metrics.reach || 0;
    analysis.total_impressions += metrics.impressions || 0;

    // Calculate platform performance score
    const performanceScore = (
      (metrics.engagement_rate || 0) * 0.3 +
      ((metrics.reach || 0) / (metrics.followers || 1)) * 0.2 +
      ((metrics.likes || 0) + (metrics.comments || 0) + (metrics.shares || 0)) * 0.3 +
      ((metrics.clicks || 0) / (metrics.impressions || 1)) * 0.2
    ) * 100;

    analysis.platform_performance[platform] = {
      score: Math.round(performanceScore),
      grade: performanceScore >= 80 ? 'A' : performanceScore >= 60 ? 'B' : performanceScore >= 40 ? 'C' : 'D',
      metrics: metrics
    };
  } else {
    analysis.failed_platforms++;
  }
}

// Calculate average engagement rate
analysis.average_engagement_rate = analysis.total_engagement / analysis.successful_platforms;

// Generate insights
if (analysis.average_engagement_rate > 5) {
  analysis.insights.push('High engagement rate across platforms - content is resonating well');
} else if (analysis.average_engagement_rate < 2) {
  analysis.insights.push('Low engagement rate - consider improving content quality and timing');
}

// Find best performing platform
const bestPlatform = Object.entries(analysis.platform_performance)
  .sort(([,a], [,b]) => b.score - a.score)[0];

if (bestPlatform) {
  analysis.insights.push(`Best performing platform: ${bestPlatform[0]} (Score: ${bestPlatform[1].score})`);
}

// Generate recommendations
if (analysis.failed_platforms > 0) {
  analysis.recommendations.push('Some platforms failed to provide metrics - check API connections');
}

if (analysis.average_engagement_rate < 3) {
  analysis.recommendations.push('Focus on creating more engaging content and posting at optimal times');
}

if (analysis.total_followers < 10000) {
  analysis.recommendations.push('Consider growing follower base through targeted content and engagement');
}

return [{ json: analysis }];

// Generate analytics report
const analysis = $input.first().json;
const timestamp = new Date().toISOString();

const report = {
  report_id: Math.random().toString(36).substr(2, 9),
  generated_at: timestamp,
  period: '6 hours',
  summary: {
    total_platforms: analysis.total_platforms,
    successful_platforms: analysis.successful_platforms,
    success_rate: ((analysis.successful_platforms / analysis.total_platforms) * 100).toFixed(1) + '%',
    total_followers: analysis.total_followers.toLocaleString(),
    average_engagement_rate: analysis.average_engagement_rate.toFixed(2) + '%'
  },
  platform_breakdown: Object.entries(analysis.platform_performance).map(([platform, data]) => ({
    platform: platform,
    grade: data.grade,
    score: data.score,
    followers: data.metrics.followers?.toLocaleString() || 'N/A',
    engagement_rate: data.metrics.engagement_rate?.toFixed(2) + '%' || 'N/A',
    reach: data.metrics.reach?.toLocaleString() || 'N/A'
  })),
  insights: analysis.insights,
  recommendations: analysis.recommendations,
  next_analysis: new Date(Date.now() + 6 * 60 * 60 * 1000).toISOString()
};

return [{ json: report }];

